from gtts import gTTSvalue = []while True:    msg = input('영단어 입력 : ')    if msg == 'ESC' or msg == 'esc':        break    value.append(msg)with open('helloEn.mp3','wb') as f:    for i in range(len(value)):        tts = gTTS(text=value[i], lang='en')        tts.write_to_fp(f)        tts.write_to_fp(f)f.close()#tts2 = gTTS(text=textKO, lang='ko')#tts.save('helloEN.mp3')#tts2.save('helloKO.mp3')